import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Companies.css';

export default function Companies() {
  const [companies, setCompanies] = useState([]);
  const [newCompany, setNewCompany] = useState({ name: '' });
  const [error, setError] = useState('');

  useEffect(() => {
    fetchCompanies();
  }, []);

  const fetchCompanies = async () => {
    try {
      const response = await axios.get('/api/companies/');
      setCompanies(response.data);
    } catch (err) {
      setError('Failed to fetch companies');
      console.error(err);
    }
  };

  const handleInputChange = (e) => {
    setNewCompany({ ...newCompany, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/companies/', newCompany);
      setNewCompany({ name: '' });
      fetchCompanies();
    } catch (err) {
      setError('Failed to add company');
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/companies/${id}`);
      fetchCompanies();
    } catch (err) {
      setError('Failed to delete company');
      console.error(err);
    }
  };

  return (
    <div className="companies-bg">
      <div className="companies-header">
        <h1>Companies</h1>
      </div>
      <div className="companies-card">
        <h2 className="section-title">Create New Company</h2>
        <form onSubmit={handleSubmit} className="company-form">
          <label htmlFor="company-name" className="company-label">Company Name</label>
          <input
            id="company-name"
            type="text"
            name="name"
            value={newCompany.name}
            onChange={handleInputChange}
            placeholder="Enter company name"
            required
            className="company-input"
          />
          <button type="submit" className="create-company-btn">Create Company</button>
        </form>
        {error && <p className="error">{error}</p>}
      </div>
      <div className="companies-card">
        <h2 className="section-title">Your Companies</h2>
        <table className="companies-table">
          <thead>
            <tr>
              <th>NAME</th>
              <th>CREATED</th>
              <th>ACTIONS</th>
            </tr>
          </thead>
          <tbody>
            {companies.map((company) => (
              <tr key={company.id}>
                <td>{company.name}</td>
                <td>{company.created_at ? new Date(company.created_at).toLocaleDateString() : ''}</td>
                <td>
                  <a href={`/companies/${company.id}/stats`} className="view-stats-link">View Stats</a>
                  <button onClick={() => handleDelete(company.id)} className="delete-link">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
